mapper.select:
select device_id, device_name, device_number, device_state,device_renew_date,device_expire_date,device_control_date,device_control_state,device_control_device_id
from t_dishwasher_device left join t_dishwasher_device_control on t_dishwasher_device.device_id=
t_dishwasher_device_control.device_control_device_id where device_id=#{deviceId,jdbcType=INTEGER} order by device_control_date desc

mapper.update:
update t_dishwasher_device
set
device_renew_date= #{deviceRenewDate,jdbcType=TIMESTAMP},
device_expire_date=#{deviceExpireDate,jdbcType=TIMESTAMP},
device_end_date=#{deviceEndDate,jdbcType=TIMESTAMP}
where device_id = #{deviceId,jdbcType=INTEGER}

mapper.select:
select device_id, device_name, device_number, device_state, device_user_id, device_purchase_date,device_renew_date,device_expire_date,device_end_date,device_style_id
from t_dishwasher_device
where device_id = #{deviceId,jdbcType=INTEGER}
